/**
 * This interface specifies all fragments that describe an event binary.
 */
export interface IEventBinary {
    /**
     * Binary name
     */
    name: string;
    /**
     * Link to this resource
     */
    self?: string;
    /**
     * The Event that the binary belongs to
     */
    source?: string;
    /**
     * Binary file length
     */
    length?: number;
    /**
     * MIME type of the binary file
     */
    type?: string;
}
