export interface IDeviceCredentials {
    /**
     * Device identifier, e.g. IMEI
     */
    id: string;
    /**
     * New password
     */
    password: string;
    /**
     * Link to this resource
     */
    self: string;
    /**
     * Tenant id for authentication
     */
    tenantId: string;
    /**
     * New username
     */
    username: string;
}
